/** @jsx jsx */
import { jsx } from "theme-ui"

function TodoForm({ addTodo }) {

  const handleSubmit = (e) => {
    e.preventDefault()
    if (!value) return
    props.addTodo(value)
  }

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        className="input"
      />
      <button type="submit">Submit</button>
    </form>
  )
}

export default TodoForm
